<body>
	<div class="container">
		<div class="innerwrap">
		
			<section class="section1 clearfix">
				<div class="textcenter">
					<h1>Contact us</h1>
				</div>
			</section>
		
			<section class="section2 clearfix">
				<div class="col2 column1 first">

                <div class="sec2map" style="overflow:hidden;width: 700px;position: relative;"><iframe width="700" height="440" src="https://maps.google.com/maps?width=700&amp;height=440&amp;hl=en&amp;q=Madurai+(Title)&amp;ie=UTF8&amp;t=&amp;z=10&amp;iwloc=B&amp;output=embed" frameborder="0" scrolling="no" marginheight="0" marginwidth="0"></iframe><div style="position: absolute;width: 80%;bottom: 10px;left: 0;right: 0;margin-left: auto;margin-right: auto;color: #000;text-align: center;"><small style="line-height: 1.8;font-size: 2px;background: #fff;">Powered by <a href="https://embedgooglemaps.com/de/">Embedgooglemaps.com/de/</a> & <a href="https://www.googlemapsgenerator.com/en/">web Traffic Geeks</a></small></div><style>#gmap_canvas img{max-width:none!important;background:none!important}</style></div><br />

                    
                
				<div class="col2 column2 last">
					<div class="sec2innercont">
						<div class="sec2addr">
							<p></p>
							<p><span class="collig">Phone :</span> +91 123456789</p>
							<p><span class="collig">Email :</span> echieparents@gmail.com</p>
						
						</div>
					</div>
					<div class="sec2contactform">
						<h3 class="sec2frmtitle">Want to know more? Drop us a mail</h3>
						<form action="">
							<div class="clearfix">
								<input class="col2 first" type="text" placeholder="FirstName">
								<input class="col2 last" type="text" placeholder="LastName">
							</div>
							<div class="clearfix">
								<input  class="col2 first" type="Email" placeholder="Email">
								<input class="col2 last" type="text" placeholder="Contact Number">
							</div>
							<div class="clearfix">
								<textarea name="textarea" id="" cols="30" rows="7">Your message here...</textarea>
							</div>
							<div class="clearfix"><input type="submit" value="Send"></div>
						</form>
					</div>

				</div>
			</section>
		
		</div>
	</div>
